mapper.update:
update tal_customercode
set model = #{model,jdbcType=VARCHAR},
  customerCode = #{customercode,jdbcType=VARCHAR}
where C_unitCode = #{cUnitcode,jdbcType=VARCHAR}

mapper.select:
select C_unitCode, model, customerCode
from tal_customercode
where C_unitCode = #{cUnitcode,jdbcType=VARCHAR}
